import React from "react";
import "./ajouterEtudiant.css";

const AjouterEtudiant = () => {
    const [formState, inputHandler] = useForm(
        {
          numDA: {
            value: '',
            isValid: false
          },
          nomEtudiant: {
            value: '',
            isValid: false
          },
          courrielEtudiant: {
            value: '',
            isValid: false
          },
          profilSortieEtudiant: {
            value: '',
            isValid: false
          }
        },
        false
      );

      const placeSubmitHandler  = async event =>  {
        event.preventDefault();
        console.log(formState.inputs); // send this to the backend!
    
        try {
          const reponseData = await sendRequest(
            "http://localhost:5000/api/places",
            "POST",
            JSON.stringify({
              titre: formState.inputs.title.value,
              description: formState.inputs.description.value,
              adresse: formState.inputs.address.value,
              createur: auth.userId
            }),
            {
              "Content-Type": "application/json",
            }
          );
    
          console.log(reponseData);
         // history.push("/");
        } catch (err) {
          console.log(err);
        }
      };

    return (
        <div className="nouveau-etudiant">
        <form>
            <div className='nouveau-etudiant__controls'>
                <div className='nouveau-etudiant__control'>
                    <label>Numero d'admission</label>
                    <input
                        type='text'
                    />
                </div>
                <div className='nouveau-etudiant__control'>
                    <label>Nom</label>
                    <input
                        type='text'
                    />
                </div>
                <div className='nouveau-etudiant__control'>
                    <label>Courriel</label>
                    <input
                        type='text'
                    />
                </div>
                <div className='nouveau-etudiant__control'>
                    <label>Profil de sortie</label>
                    <input
                        type='text'
                    />
                </div>
            </div>
            <div className='nouveau-etudiant__actions'>
                <button type='submit'>Ajouter Étudiant</button>
            </div>
        </form>
        </div>
    );
  }
  
  export default AjouterEtudiant;
