import React from "react";
import "./ajouterStage.css";

function AjouterStage() {
    const {error, sendRequest, clearError } = useHttpClient();
    const [formState, inputHandler] = useForm(
        {
          nomContact: {
            value: '',
            isValid: false
          },
          courrielContact: {
            value: '',
            isValid: false
          },
          numContact: {
            value: '',
            isValid: false
          },
          nomEntreprise: {
            value: '',
            isValid: false
          },
          adresseEntreprise: {
            value: '',
            isValid: false
          },
          typeStage: {
            value: '',
            isValid: false
          },
          nbPosteDispo: {
            value: '',
            isValid: false
          },
          description: {
            value: '',
            isValid: false
          },
          renumeration: {
            value: '',
            isValid: false
          }
        },
        false
      );

      const stageSubmitHandler  = async event =>  {
        console.log("test")
        event.preventDefault();
        console.log(formState.inputs);
    
        try {
          const reponseData = await sendRequest(
            "http://localhost:5000/api/stages/ajouterStage",
            "POST",
            JSON.stringify({
                nomContact: formState.inputs.nomContact.value,
              nomEtudiant: formState.inputs.nomEtudiant.value,
              courrielEtudiant: formState.inputs.courrielEtudiant.value,
              profilSortieEtudiant: formState.inputs.profilSortieEtudiant.value,
            }),
            {
              "Content-Type": "application/json",
            }
          );
          console.log(reponseData);
        } catch (err) {
          console.log(err);
        }
      };

    return (
        <div className="nouveau-stage">
        <form onSubmit={stageSubmitHandler}>
            <div className='nouveau-stage__controls'>
                <div className='nouveau-stage__control'>
                    <label>Numero d'admission</label>
                    <input
                        id="numDA"
                        type='text'
                        onInput={inputHandler}
                    />
                </div>
                <div className='nouveau-stage__control'>
                    <label>Nom</label>
                    <input
                        id="nom"
                        type='text'
                        onInput={inputHandler}
                    />
                </div>
                <div className='nouveau-stage__control'>
                    <label>Courriel</label>
                    <input
                        id="courriel"
                        type='text'
                        onInput={inputHandler}
                    />
                </div>
                <div className='nouveau-stage__control'>
                    <label>Profil de sortie</label>
                    <input
                        id="profilSortie"
                        type='text'
                        onInput={inputHandler}
                    />
                </div>
            </div>
            <div className='nouveau-stage__actions'>
                <button type='submit' disabled={!formState.isValid}>Ajouter Étudiant</button>
            </div>
        </form>
        </div>
    );
  }
  
  export default AjouterStage;
