import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "./listeEtudiant.css";
import ListeEtudiant from "../components/ListeEtudiant";

const ListeEtudiantPage = () => {
    const [loadedEtudiants, setLoadedEtudiants] = useState();
    const { error, sendRequest, clearError } = useHttpClient();
  
    const userId = useParams().userId;
  
    useEffect(() => {
      const fetchEtudiants = async () => {
        try {
          const responseData = await sendRequest(
            `http://localhost:5000/api/listeEtudiant/${userId}`
          );
          setLoadedEtudiants(responseData.etudiants);
        } catch (err) {}
      };
      fetchEtudiants();
    }, [sendRequest, userId]);
  
    const etudiantDeletedHandler = (deletedEtudiantId) => {
      setLoadedEtudiants((prevEtudiants) =>
        prevEtudiants.filter((etudiant) => etudiant.id !== deletedEtudiantId)
      );
    };

    return (
        <React.Fragment>
            <ErrorModal error={error} onClear={clearError} />
            {loadedEtudiants && (
                <ListeEtudiant items={loadedEtudiants} onDeleteEtudiant={etudiantDeletedHandler} />
            )}
        </React.Fragment>
    );
  }
export default ListeEtudiantPage;
