import React from "react";
import "./PageFAQ.css";

function Faq() {
    return (
        <div>
            <section>
                <h1>Édition 2023</h1>
                <h2>Foire aux questions - FAQ</h2>
                <br/>
                <h3>Est-ce que le stage est obligatoire?</h3>
                <li>Le stage de fin d'études en informatique est obligatoire pour l'obtention du diplôme collégial.</li>

                <h3>Quel est l'horaire de l'étudiant durant les stages?</h3>
                <li>L'étudiant doit respecter l'horaire de l'entreprise durant son stage.</li>

                <h3>Est-ce que l'étudiant travaille pendant les journées pédagogiques et les journées de rattrapage?</h3>
                <li>L'étudiant doit respecter l'horaire de l'entreprise durant son stage et ce même durant les journées pédagogiques et de rattrapage.</li>

                <h3>Quelle est la durée d'un stage de fin d'études?</h3>
                <li>La durée du stage est de 15 semaines pour les deux profils de sortie (réseaux et programmation).</li>

                <h3>Quelles sont les dates prévues pour les stages?</h3>
                <li>Les stages sont prévus du 21 janvier au 3 mai 2019.</li>

            </section>
        </div>
    );
  }
  
  export default Faq;
